import React from 'react'
import ReactDOM from 'react-dom'


const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>
      <Header course={course} />
      <Content exercises1={exercises1} part1={part1}
        exercises2={exercises2} part2={part2}
        exercises3={exercises3} part3={part3} />
      <Total exercises1={exercises1} exercises2={exercises2}
        exercises3={exercises3} />
    </div>
  )
}

const Header = (muuttuja) => {
  return (
    <div>
      <h1>{muuttuja.course}</h1>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part part={props.name} exercises={props.exercises} />
      <Part part={props.part2} exercises={props.exercises2} />
      <Part part={props.part3} exercises={props.exercises3} />
    </div>
  )

}

const Part = (props) => {
  return (
    <div>
      <p> {props.name}{props.exercises}</p>
    </div>
  )

}

const Total = (props) => {
  return (
    <div>
      <p>Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}</p>
    </div>
  )
}



ReactDOM.render(<App />, document.getElementById('root'))
