import React from 'react'
import ReactDOM from 'react-dom'


const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>

      <Header course={course} />
      <Content props={part1} props1={part2} props2={part3} />
      <Total props1={part1} props2={part2} props3={part3} />
    </div>
  )
}

const Header = (muuttuja) => {

  return (
    <div>
      <h1>{muuttuja.course}</h1>
    </div>
  )
}

const Content = (props, props1, props2) => {
  return (
    <div>
      <Part props = {props} />
      <Part props = {props1} />
      <Part props = {props2} />
    </div>
  )

}

const Part = (props) => {

  return (
    <div>
      <p> {props.name} {props.exercises}</p>
    </div>
  )

}

const Total = (props1, props2, props3) => {

  return (
    <div>

      <p>Number of exercises {props1.get + props2.get + props3.get} </p>
    </div>
  )
}



ReactDOM.render(<App />, document.getElementById('root'))
