import React from 'react'


const Person = ({ person }) => { 

    removePerson = removePerson
    return (

        <div >
            <p> {person.name} {person.number}

                <button onClick={() =>
                    removePerson(person.name)
                }>Delete</button>
            </p>
        </div>
    )
}


const ListPeople = (props) => {
    return (
        <div>
            {props.namesToShow.map((name) =>
                <Person key={name.name} person={name} removePerson={props.removePerson} />


            )}

        </div>
    )
}

export default ListPeople
