import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const App = () => {
  const [persons, setPersons] = useState([

  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewFilter] = useState('')
  const [showAll] = useState(false)

  const handleNewName = (event) => {
    console.log(event.target.value)

    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  const handleFilter = (event) => {
    console.log(event.target.value)
    setNewFilter(event.target.value)
  }

  const namesToShow = showAll
    ? persons
    : persons.filter(person => person.name.toLowerCase().includes(newFilter.toLowerCase()))

  const morePeople = (event) => {
    event.preventDefault()
    const nameObject = {
      name: newName,
      number: newNumber
    }
    if (persons.some(person => person.name === nameObject.name)) {
      alert(nameObject.name + " is already added to phonebook")

    } else {
      setPersons(persons.concat(nameObject))
      setNewName('')
      setNewNumber('')
    }
  }

  return (
    <div>
      <h1>Phonebook</h1>
      <ShowFilter newFilter={newFilter} handleFilter={handleFilter} />

      <h2>Add A New Number</h2>

      <PersonForm newName={newName} handleNewName={handleNewName}
        newNumber={newNumber} handleNewNumber={handleNewNumber} morePeople={morePeople} />


      <h2>Numbers</h2>
      <ListPeople namesToShow={namesToShow} />
    </div >
  )

}

const ShowFilter = (props) => {
  return (
    <div>

      filter shown with <input value={props.newFilter} onChange={props.handleFilter} />
    </div>
  )
}

const Person = ({ person }) => {
  return (
    <p> {person.name} {person.number} </p>
  )
}

const ListPeople = (props) => {
  return (
    <div>
      {props.namesToShow.map((name) =>
        <Person key={name.name} person={name} />
      )}
    </div>
  )
}



const PersonForm = (props) => {
  return (
    <form onSubmit={props.morePeople}>
      <div>
        <p>
          name: <input value={props.newName} onChange={props.handleNewName} />
        </p>
        <p>
          number: <input value={props.newNumber} onChange={props.handleNewNumber} />
        </p>

        <button type="submit">add</button>
      </div>
    </form>
  )
}

export default App

ReactDOM.render(<App />, document.getElementById('root'))
