import React from 'react'
import { useSelector} from 'react-redux'


const Notification = () => {

  const notification = useSelector(state => state.notifications)
  const style = {
    border: 'solid',
    padding: 10,
    borderWidth: 1
  }
  if (notification === '' || notification === null) {
    return (
      <div></div>
    )
  }
  return (
    <div>
      <div style={style}>
        {notification}
      </div>
    </div>

  )
}

export default Notification
