const initialState = {
  good: 0,
  ok: 0,
  bad: 0
}

const counterReducer = (state = initialState, action) => {
  console.log(action)
  switch (action.type) {
    case 'GOOD':
      return good + 1
    case 'OK':
      return ok +1
    case 'BAD':
      return bad +1
    case 'ZERO':
      return 0
    default: return state
  }
  
}

export default counterReducer
